mapper.select:
SELECT 	COUNT(EDU_DETIDX)
FROM 	EDU_DETAIL
WHERE 	EDU_DETAILSTATUS = 'EDUDT01' AND EDU_IDX = #{idx}

		SELECT 	NN.*
		FROM    (
		        SELECT ROWNUM RN, N.*
		        FROM    (
		                SELECT 		*
		                FROM 		EDU_DETAIL
		                WHERE 		EDU_DETAILSTATUS = 'EDUDT01' AND EDU_IDX = #{idx}
		                ORDER BY 	EDU_IDX DESC
		                ) N
        		) NN
		WHERE 	RN BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE EDU_DETAIL
SET EDU_DETAILSTATUS = 'EDUDT02' 
WHERE EDU_DETIDX = #{temp}

