SET EDU_TARGET = #{edu_target}, EDU_IMPT_CONTENTS = #{edu_impt_contents}, EDU_DETAILADDR = #{edu_detailaddr}, EDU_IDX = #{edu_idx}, EDU_DETAILNAME = #{edu_detailname}, EDU_PRICE = #{edu_price}, EDU_DETAILTARGET = #{edu_detailtarget}, EDU_PERIOD_START = #{edu_period_start}, EDU_PERIOD_END = #{edu_period_end}, EDU_DETAILTIME = #{edu_detailtime} WHERE EDU_DETIDX = #{edu_detidx}
