mapper.update:
UPDATE MU_MEMBER
SET MEM_ID = #{mem_id}, MEM_PWD = #{mem_pwd}, MEM_NAME=#{mem_name},
	MEM_PHONE = #{mem_phone},
	MEM_CELLPHONE = #{mem_cellphone}, MEM_EMAIL = #{mem_email}, 
	MEM_ADDR = #{mem_addr}
WHERE MEM_ID = #{mem_id}

UPDATE MU_MEMBER
SET MEM_JOIN_STATUS = 1
WHERE MEM_ID = #{mem_id}
